#include <bits/stdc++.h>
#include <string>
#define lli long long int
using namespace std;
lli gcd(lli a, lli b){
	if(b==0)
		return a;
	return gcd(b, a%b);
}
lli lcm(lli a, lli b){
	return (a*b) /(gcd(a,b));
}
bool isPrime(int n){
	if(n==2)
		return true;
	else{
		if(n%2==0)
			return false;
		for(int i = 3, end = sqrt(n) ; i<= end ; i+=2){
			if(n%i==0)
				return false;
		}
			return true;
	}
}
int main(){
	string s = "";
	int n;
	cin >> n;
	unsigned long long  best = 0;
	for(unsigned int i = 0 ; i + n < s.length() ; i++){
		unsigned long long current =1;
		for(unsigned int j = 0 ; j < n ; j++){
			current *= s[i+j] - '0';
		}
		if(current > best)
			best = current;
	}
	cout << best;
}
